import React, { Component } from 'react';
import { Table } from 'reactstrap';

class DispensaryTable extends Component {
  state = {
    PatientID : ' ',
    Drugs: '',
    Dossage:'',
    Quantity:'',

     dispensary: []


  };
  handleChange = e => {
    this.setState({
      [e.target.name] : e.target.value,
    });
  };

  handleDelete = remove => {
    let lily = this.state.hcf.filter(item => item !== remove);
    this.setState({ hcf : lily});
  }
  render() {
    return (
      <div>
        <center>
          <h5>Dispensary Table</h5>
        </center>

        <Table>
          <thead>
            <tr>
              <th>PatientID</th>
              <th>Drugs</th>
              <th>Dossage</th>
              <th>Quantity</th>
              <th> <Button outline color="danger">Delete</Button></th>
            </tr>
          </thead>
          <tbody>
            <tr>
            {this.state.dispensary.map((item, index) => (
             <tr key={index}> 
              <td>{item.PatientID}</td>
             <td>{item.Drugs}</td>
             <td>{item.Dossage}</td>  
             <td>{item.Quantity}</td>
             <td><button onClick={()=> this.handleDelete(item)}>
               DELETE
             </button></td>

             </tr>
           )
           )}
            </tr>
          </tbody>

        </Table>
      </div>
    );
  }
}
export default DispensaryTable;
