import React, { Component } from "react";


class HospitalRegistration extends Component {
  state={
    hcfNo : ' ',
    hcfName : '',
    address : '',
    phone : '',
    contactPerson : '',
    email : '',

     hcf : []

  };









  handleChange = e => {
    this.setState({
      [e.target.name] : e.target.value,
    });
  };



validate=()=>{
 let errorhcfNo = '';
 let  errorhcfName = '';
 let erroraddress = '';
 let errorphone = '';
 let errorcontactPerson = '';
 let erroremail = '';

if(!this.state.errorhcfNo)
{
  errorhcfNo=""
}
 if(!this.state.hcfName){
   errorhcfName="required!!!"
 }
if(!this.state.address){
  erroraddress="required!!!"
}
if(!this.state.phone){
  errorphone="required!!!"
}
if(!this.state.contactPerson){
  errorcontactPerson="required!!!"
}

 if(!this.state.email.includes('@')){
   erroremail =' invalid email';

 }
 if(erroremail || errorcontactPerson ||errorphone ||erroraddress||errorhcfName || errorhcfNo){
   this.setState({erroremail,errorcontactPerson,errorphone,erroraddress,errorhcfName,errorhcfNo});
   return false;
 }return true;
}





  handleSubmit = () => {
    const isValid= this.validate();
    if(isValid){
    this.setState(prevState => ({
     hcf: prevState.hcf.concat({
      hcfNo: this.state.hcfNo,
      hcfName: this.state.hcfName,
      address: this.state.address,
      phone: this.state.phone,
      contactPerson : this.state.contactPerson ,
      email: this.state.email,
     }),
    hcfNo : '',
    hcfName : '',
    address : '',
    phone : '',
    contactPerson : '',
    email : '',
    }));
    this.refs.hcfNo.focus()
  };}



  handleChange = ({ target: { name, value } }) => {
    this.setState({
        error: '',
      [name]: value,
    });
  }



  handleDelete = remove => {
    let lily = this.state.hcf.filter(item => item !== remove);
    this.setState({ hcf : lily});
  }






  handleKeypress (e) {
    const characterCode = e.key
    if (characterCode === 'Backspace') return

    const characterNumber = Number(characterCode)
    if (characterNumber >=0 && characterNumber <= 9) {
      if (e.currentTarget.value && e.currentTarget.value.length) {
        return
      } else if (characterNumber === -1) {
        e.preventDefault()
      }
    } else {
      e.preventDefault()
    }
  }








 render(){
    return(

      <div id="color">

       <h1 className='head'>HOSPITAL REGISTRATION</h1>
         <div  scr="src/helo.jpg" width="503px" height="500px" alt="">
       <form>
       <div style={{color:"red"}}>{this.state.errorhcfNo}</div>

       <input 
         id='in1'
         name='hcfNo'
         value={this.state.hcfNo}
         required="required"
         ref="hcfNo"
         placeholder='HCF NO:'
         type='number'
         onChange={this.handleChange}
         onKeyDown={this.handleKeypress} min='1' step='1'
         /><br></br><br></br>



       <div  style={{color:"red"}}>{this.state.errorhcfName}</div>
         <input
         name='hcfName'
         id='in2'
         required="required"
         value={this.state.hcfName}
         placeholder='HCF NAME'
         onChange={this.handleChange}
         /><br></br><br/>


     <div  style={{color:"red"}}>{this.state.erroraddress}</div>
         <input 
         name='address'
         id='in3'
         required="required"
         value={this.state.address}
         type='text'
         placeholder='ADDRESS'
         onChange={this.handleChange}
         /><br></br><br/>


        <div  style={{color:"red"}}>{this.state.errorphone}</div>
         <input 
         name='phone'
         id='in4'
         required="required"
         value={this.state.phone}
         type='number'
         placeholder='PHONE'
         onKeyDown={this.handleKeypress} min='1' step='1'
         onChange={this.handleChange}
         /><br></br><br></br>


         <div  style={{color:"red"}}>{this.state.errorcontactPerson}</div>
         <input 
         name='contactPerson'
         id='in5'
         required="required"
         value={this.state.contactPerson}
         placeholder='CONTACT PERSON'
         onChange={this.handleChange}
         /><br></br><br></br>


        <div  style={{color:"red"}}>{this.state.erroremail}</div>  
         <input 
         name='email' 
         id='in6'
         value={this.state.email}
         placeholder='EMAIL'
         type='email'
         required="required"
         onChange={this.handleChange}
         /><br></br>




          <br />



         <input  style={{backgroundColor:"gray"}} onClick={this.handleSubmit}     type="submit"/>
         </form>
         </div>





      <br /> <br /> <br />
       <div>
         <table id='table'>
           <thead>
           <tr>
              <th>HCF No:</th>
             <th>HCF Name:</th>
             <th>ADDRESS:</th>
             <th>PHONE:</th>
             <th>CONTACT:</th>
             <th>EMAIL</th>
           </tr>
           </thead>
           <tbody>
           {this.state.hcf.map((item, index) => (
             <tr key={index}> 
              <td>{item.hcfNo}</td>
             <td>{item.hcfName}</td>
             <td>{item.address}</td>  
             <td>{item.phone}</td>
             <td>{item.contactPerson}</td>
             <td>{item.email}</td>
             <td><button onClick={()=> this.handleDelete(item)}>
               DELETE
             </button></td>

             </tr>

           )
           )}
           </tbody>
         </table>
       </div>
      </div>

    );
  }
} 


export default HospitalRegistration;
