class HospitalRegistration extends Component {
  state={
    hcfNo : ' ',
    hcfName : '',
    address : '',
    phone : '',
    contactPerson : '',
    email : '',
    isOpen: false,
    location: null,
    locationStatusMessage: 'null',
    locationStatus: false,
    locationLoading: false,
    isShow: false,

     hcf : []

  };


  // componentDidMount(){
