import React, { Component } from "react";
import { Table, Form,Button,Input,Col,Row,FormGroup} from 'reactstrap';
import Map from './Map'


class HospitalRegistration extends Component {
  state={
    facilityNo : ' ',
    facilityName: '',
    facilityAdress: '',
    contactPerson : '',
    email : '',
    phoneNumber : '',

    Hr : []

  };


  handleChange = e => {
    this.setState({
      [e.target.name] : e.target.value,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { facilityNo, facilityName, facilityAdress,contactPerson,phoneNumber,email} = this.state;

    if(facilityNo===''||facilityName===''||facilityAdress===''||phoneNumber===''||email===''||contactPerson===''){
        this.setState({ error: 'their is missing place please check the' })
    } else {
        this.setState(prevState => ({ 
            //modalIsOpen: false,
            Hr: prevState.Hr.concat({
                facilityNo: this.state.facilityNo,
                facilityName: this.state.facilityName,
                facilityAdress: this.state.facilityAdress,
                phoneNumber: this.state.phoneNumber,
                email: this.state.email,
                contactPerson: this.contactPerson,

            }),
        }), () => this.resetForm());
    }

  }


// validate=()=>{
//  let errorfacilityNo = '';
//  let  errorfacilityName = '';
//  let errorfacilityAdress = '';
//  let errorcontactPerson = '';
//  let erroremail = '';
//  let errorphoneNumber = '';


// if(!this.state.errorfacilityNo)
// {
//   errorfacilityNo="required!!!"
// }
//  if(!this.state.errorfacilityName){
//    errorfacilityName="required!!!"
//  }
// if(!this.state.errorfacilityAdress){
//   errorfacilityAdress="required!!!"
// }
// if(!this.state.errorcontactPerson){
//   errorcontactPerson="required!!!"
// }


//  if(!this.state.email.includes('@') ){
//   erroremail="required!!!"

//  }
//  if(!this.state.errorphoneNumber){
//   errorphoneNumber="required!!!"
// }


//  if(errorfacilityNo ||
//    errorfacilityName||
//    errorfacilityAdress ||
//    errorcontactPerson||
//    erroremail || 
//    errorphoneNumber
//   ){
//    this.setState({errorfacilityNo,
//     errorfacilityName,
//     errorfacilityAdress,
//     errorcontactPerson,
//     erroremail,
//     errorphoneNumber
//     });
//    return false;
//  }return true;
// }


  handleSubmit = () => {
    // e.preventDefault()
    const isValid=this.handleSubmit //this.validate();
    if(isValid){
    this.setState(prevState => ({
     Hr: prevState.Hr.concat({
      facilityNo: this.state.facilityNo,
      facilityName: this.state.facilityName,
      facilityAdress: this.state.facilityAdress,
      contactPerson: this.state.contactPerson,
      email : this.state.email,
      phoneNumber: this.state.phoneNumber,

     }),
    facilityNo : '',
    facilityName : '',
    facilityAdress : '',
    contactPerson : '',
    email : '',
    phoneNumber:""


    })

    );
    // this.refs.Hr.focus()
    console.log(this.state.email)
  };}




  handleDelete = remove => {
    let king = this.state.Hr.filter(item => item !== remove);
    this.setState({ Hr : king});
  }






  // handleKeypress (e) {
  //   const characterCode = e.key
  //   if (characterCode === 'Backspace') return

  //   const characterNumber = Number(characterCode)
  //   if (characterNumber >=0 && characterNumber <= 9) {
