import React, {Component} from 'react'
import { Col, Row, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import { Map, GoogleApiWrapper } from 'google-maps-react';




class HospitalRegistration extends Component{
    state={
        facilityNo:'',
        facilityName:'',
        facilityAdress:'',
        contactPerson:'',
        email:'',
        phoneNumber:'',
        location:'',
        error: '',
        Hr:[]
    }
    handleChange = e => {
      this.setState({
        [e.target.name] : e.target.value,
      });
    };

  handleDelete = remove => {
    let fahad = this.state.hcf.filter(item => item !== remove);
    this.setState({ Hr : fahad});
  }

  handleSubmit = () => {
    const isValid= this.validate();
    if(isValid){
    this.setState(prevState => ({
     Hr: prevState.hcf.concat({
      facilityNo: this.state.facilityNo,
      facilityName: this.state.facilityName,
      facilityAdress: this.state.facilityAdress,
      contactPerson: this.state.contactPerson,
      email : this.state.email ,
      phoneNumber: this.state.phoneNumber,
     }),

    }));

  };}





    render(){
        return(
            <div>
                 <Form>
      <Row form>
        <Col md={6}>
        <FormGroup>

        <Input type="select" name="select" id="exampleSelect" value={this.state.facilityNo}>
        <option>Facility number</option>
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
          <option>6</option>
          <option>7</option>
          <option>8</option>
          <option>9</option>
          <option>10</option>
        </Input>
      </FormGroup>
        </Col>
        <Col md={6}>
          <FormGroup> 
            <Input value={this.state.facilityName} type="text" name="text" id="exampleText" placeholder="facilty Name" />
          </FormGroup>
        </Col>
      </Row>
      <FormGroup>

        <Input value={this.state.facilityAdress} type="text" name="address" id="exampleAddress" placeholder="Facility Address"/>
      </FormGroup>
      <FormGroup>

        <Input value={this.state.contactPerson} type="text" name="address" id="exampleAddress" placeholder="Contact person"/>
      </FormGroup>
      <Row form>
        <Col md={6}>
          <FormGroup>

            <Input value={this.state.email} type="number" name="city" id="exampleCity" placeholder="Phone number"/>
          </FormGroup>
        </Col>
        <Col md={6}>
        <FormGroup>

           <Input value={this.state.email} type="email" name="email" id="exampleEmail" placeholder="Email" />
         </FormGroup>
        </Col>

      </Row>
      {/* <div>
        <Map/>
    </div> */}

      <center><Button type="submit" onClick={this.handleSubmit} >Submit</Button></center>
    </Form>

            </div>
        )
    }

}
export default HospitalRegistration;
