//first you need to import all component you need
import React, {Component} from 'react'
import { Col, Row, Button, Form, FormGroup, Label, Input, ButtonToggle } from 'reactstrap';

//then i have created class component
class Hospital extends Component{
        state={
            facilityNo : ' ',
            facilityName: '',
            facilityAdress: '',
            contactPerson : '',
            email : '',
            phoneNumber : '',
            error:'',
            hr:[]
        };
        //to execute javascript when user is presing key
        handleChange=(e)=>{
            this.setState({
               [ e.target.name]:e.target.value
            });
        };
        //the function thst will handle the table resect
        resetForm = () => {
          this.setState({
            patientID:'',
            drugs:'',
            dosage:"",
            quantity_dispense:''
          })
      }
        //this is the function that will submit the form
        handleSubmit = (e) => {
          e.preventDefault();
          const { facilityNo, facilityName, facilityAdress,contactPerson,email,phoneNumber } = this.state;

          if(facilityNo===''||facilityName===''||facilityAdress===''||contactPerson===''||email===''||phoneNumber===''){
              this.setState({ error: 'Please complete the form' })
          } else
          {
              this.setState(prevState => ({ 
                  modalIsOpen: false,
                  hr: prevState.hr.concat({
                      facilityNo: this.state.facilityNo,
                      facilityName: this.state.facilityName,
                      facilityAdress: this.state.facilityAdress,
                      contactPerson: this.state.contactPerson,
                      email: this.state.email,
                      phoneNumber: this.state.phoneNumber,

                  }),
              }), () => this.resetForm());
          }

        }
        render(){
            return(
    <div>
   <div> <Form>
      <Row form>
        <Col md={6}>
          <FormGroup>

            <Input type="number" 
            onKeyDown={this.state.handleChange}
            value={this.state.facilityNo}
            name='facilityNo'
            required=''
            min='0'
              placeholder="Facility Number" />
          </FormGroup>
        </Col>
        <Col md={6}>
          <FormGroup>

            <Input type="text"
            onKeyDown={this.state.handleChange}
            value={this.state.facilityName}
            name='facilityName'
            required=''
             placeholder="Facility Name"/>
          </FormGroup>
        </Col>
      </Row>
      <Row form>
        <Col md={6}>
          <FormGroup>

            <Input type="text"
            onKeyDown={this.state.handleChange}
            value={this.state.facilityAdress}
            required=''
            name='facilityAdress'
               placeholder="Facilty Adress" />
          </FormGroup>
        </Col>
        <Col md={6}>
          <FormGroup>

            <Input type="email" 
            onKeyDown={this.state.handleChange}
            name='email'
            value={this.state.email}
            required=''
            placeholder="Email"/>
          </FormGroup>
        </Col>
      </Row>
      <Row form>
        <Col md={6}>
          <FormGroup>

            <Input type="number"  
            onKeyDown={this.state.handleChange}
            value={this.state.phoneNumber}
            min='0'
            required=''
            name='phoneNumber'
             placeholder="Phone Number" />
          </FormGroup>
        </Col>
      </Row>
      <ButtonToggle color="success">Add Location</ButtonToggle>
    </Form></div>
    <div>
    <Table size="sm">
      <thead>
        <tr>
                <td>Facility Number</td>
                <td>Facility Name</td>
                <td>Facility Adress</td>
                <td>Email</td>
                <th>Phone Number</th>
                <th>Delete</th>
        </tr>
      </thead>
      <tbody>
      <tbody>
           {this.state.hr.map((item, index) => (
             <tr key={index}> 
              <td>{item.facilityNo}</td>
             <td>{item.facilityName}</td>
             <td>{item.facilityAdress}</td>  
             <td>{item.contactPerson}</td>
             <td>{item.email}</td>
             <td>{item.phoneNumber}</td>

             <td><Button 

             className="btn btn-danger"onClick={()=> this.handleDelete(item)

             }>
               Delete
             </Button></td>

             </tr>

           )
           )}
           </tbody>
      </tbody>
    </Table>
    </div>
                </div>
            )
        }
}
export default Hospital;
